<?phpfunction renomeiaCV($string){    $carac_ent = array( 'ä','ã','à','á','â','ê','ë','è','é','ï','ì','í','ö','õ','ò','ó','ô','ü','ù','ú','û','À','Á','É','Í','Ó','Ú','ñ','Ñ','ç','Ç',' ','-','(',')',',',';',':','|','!','"','#','$','%','&','/','=','?','~','^','>','<','ª','º' );    // matriz de saída    $carac_sai   = array( 'a','a','a','a','a','e','e','e','e','i','i','i','o','o','o','o','o','u','u','u','u','A','A','E','I','O','U','n','n','c','C','_','_','_','_','_','_','_','_','_','_','_','_','_','_','_','_','_','_','_','_','_','_','_' );        $renomeia = str_replace($carac_ent,$carac_sai, $string);    $renomeia = strtolower($renomeia);    $data = date('Y-m-d');    $renomeia = $data.'_'.$renomeia;    return $renomeia;}$doc = JFactory::getDocument();$doc->addStyleSheet('components/com_vacancies/css/stylevacancies.css');$dados = $_POST;$nome = $dados['nome'];$email = $dados['email'];$cand = $dados['voce'];$file = JRequest::getVar('file-original', null, 'files', 'array');if (!empty($file['name'])) {    jimport('joomla.filesystem.file');    $filename = JFile::makeSafe($file['name']);    $cv = renomeiaCV($nome);    $src = $file['tmp_name'];    $dest = JPATH_SITE . DIRECTORY_SEPARATOR . "curriculos" . DIRECTORY_SEPARATOR . $cv .'.'.JFile::getExt($filename);    if (( strtolower(JFile::getExt($filename)) == 'pdf') || ( strtolower(JFile::getExt($filename)) == 'txt') || ( strtolower(JFile::getExt($filename)) == 'doc') || ( strtolower(JFile::getExt($filename)) == 'docx')) {        JFile::upload($src, $dest);        //==============================================================================//ENVIANDO EMAIL        $app = JFactory::getApplication();        $mailfrom = $app->get('mailfrom');        $fromname = $app->get('fromname');        $sitename = $app->get('sitename');        $name = $nome;        $e_mail = $email;        $subject = 'Currículo: ' . $name;// Prepare email body        $body = '<html>'                . '<body>'                . '<div>'                . '<h2>E-mail via site '.$sitename.'</h2>'                . '</div>'                . '<div style="font-size: 14px; font-family: Century Gothic;">'                . '<p>Nome: ' . $name . '</p>'                . '<p>E-mail: ' . $e_mail . '</p>'                . '<p>Sobre o candidato: ' . $cand . '</p>'                . '</div>'                . '</body>'                . '</html>';        $mail = JFactory::getMailer();        $mail->addRecipient($mailfrom);        $mail->addReplyTo($e_mail, $name);        $mail->setSender(array($mailfrom, $fromname));        $mail->isHtml();        $mail->setSubject($subject);        $mail->setBody($body);        $mail->AddAttachment(JPATH_SITE . DIRECTORY_SEPARATOR . "curriculos" . DIRECTORY_SEPARATOR . $cv .'.'.JFile::getExt($filename), $cv .'.'.JFile::getExt($filename));        $sent = $mail->Send();        echo '<div class="enviado">'        . '<h1>Candidatura enviada com Sucesso!</h1>'        . '<p>Obrigado</p></div>';//==============================================================================    } else {        echo '<div class="erro">'        . '<h3>Candidatura não foi realizada! Tipo de arquivo inválido!</h3>'        . '<button class="voltar" onclick="javascript:history.go(-1)">Alterar</button>'        . '</div>';    }}